var buttonWrapper = document.createElement("div");
buttonWrapper.innerHTML = svgString.trim();

var newBtn = document.createElement("button");
newBtn.className = "ytp-fullscreen-button ytp-button";
newBtn.title = "Bookmark";
newBtn.append(buttonWrapper.firstChild);
newBtn.onclick = function() {
  if (!iFrameInitializd) {
    initIFrame();
    document.getElementById("theIframe").onload = function() {
      performBookmarkIconClickAction();
    };
  } else {
    performBookmarkIconClickAction();
  }
};

function performBookmarkIconClickAction() {
  document.getElementsByClassName("video-stream")[0].pause();
  extractInfoFromCurrentVideo();
  document.getElementById("draggableWrapper").style.display = "block";
}

document.getElementsByClassName("ytp-right-controls")[0].append(newBtn);

var iFrameInitializd = false;
function initIFrame() {
  var draggableWrapper = document.createElement("div");
  var mainPageUrl = chrome.extension.getURL("mainPage.html");
  draggableWrapper.innerHTML =
    '<div id="draggableWrapper" style="width:400px; height:500px; top:50px; right:20px; position: absolute; z-index: 2147483647 !important; background-color: #f1f1f1; text-align: center; border: 1px solid #d3d3d3;"><div id="draggableWrapperHeader" style="padding: 10px; font-size: 10pt; cursor: move; background-color: #2196F3; color: #fff;">Press to drag <button style="float:right;" onclick="document.getElementById(\'draggableWrapper\').style.display = \'none\';">X</button></div><iframe id="theIframe" src="' +
    mainPageUrl +
    '" style="width:100%; height:100%; display:unset;"/></div>';

  document.body.append(draggableWrapper.firstChild);
  iFrameInitializd = true;

  //Make the DIV element draggagle:
  dragElement(document.getElementById("draggableWrapper"));
}

function extractInfoFromCurrentVideo() {
  var theScript = document.createElement("script");
  theScript.type = "text/javascript";
  var code = `window.postMessage(
    { 
        type: "CUR_VIDEO_INFO", 
        args: {
            title : document.getElementsByClassName("ytp-title-link yt-uix-sessionlink")[0].innerText,
            url : document.getElementsByClassName("ytp-title-link yt-uix-sessionlink")[0].href,
            playbackTime: document.getElementsByClassName("video-stream")[0].getCurrentTime()
        }
    }, "*");`;
  try {
    theScript.appendChild(document.createTextNode(code));
    document.body.appendChild(theScript);
  } catch (e) {
    theScript.text = code;
    document.body.appendChild(theScript);
  }
}

let videoInfo = {
  id: "",
  title: "",
  playbackTime: 0
};

// listen for call from page
window.addEventListener(
  "message",
  function(event) {
    // We only accept messages from ourselves
    if (event.source != window) return;

    if (event.data.type && event.data.type == "CUR_VIDEO_INFO") {
      videoInfo.id = getVideoIdFromUrl(event.data.args.url);
      videoInfo.title = event.data.args.title;
      videoInfo.playbackTime = event.data.args.playbackTime;

      console.log("received args from main page", JSON.stringify(videoInfo));

      document
        .getElementById("theIframe")
        .contentWindow.postMessage(videoInfo, "*");
    }
  },
  false
);

function getVideoIdFromUrl(url) {
  var video_id = window.location.search.split("v=")[1];
  var ampersandPosition = video_id.indexOf("&");
  if (ampersandPosition != -1) {
    video_id = video_id.substring(0, ampersandPosition);
  }
  return video_id;
}

function dragElement(elmnt) {
  var pos1 = 0,
    pos2 = 0,
    pos3 = 0,
    pos4 = 0;
  if (document.getElementById(elmnt.id + "Header")) {
    /* if present, the header is where you move the DIV from:*/
    document.getElementById(elmnt.id + "Header").onmousedown = dragMouseDown;
  } else {
    /* otherwise, move the DIV from anywhere inside the DIV:*/
    elmnt.onmousedown = dragMouseDown;
  }

  function dragMouseDown(e) {
    e = e || window.event;
    e.preventDefault();
    // get the mouse cursor position at startup:
    pos3 = e.clientX;
    pos4 = e.clientY;
    document.onmouseup = closeDragElement;
    // call a function whenever the cursor moves:
    document.onmousemove = elementDrag;
  }

  function elementDrag(e) {
    e = e || window.event;
    e.preventDefault();
    // calculate the new cursor position:
    pos1 = pos3 - e.clientX;
    pos2 = pos4 - e.clientY;
    pos3 = e.clientX;
    pos4 = e.clientY;
    // set the element's new position:
    elmnt.style.top = elmnt.offsetTop - pos2 + "px";
    elmnt.style.left = elmnt.offsetLeft - pos1 + "px";
  }

  function closeDragElement() {
    /* stop moving when mouse button is released:*/
    document.onmouseup = null;
    document.onmousemove = null;
  }
}
